$('path').each(function(i,el){
    var length = el.getTotalLength();
    $(el).data('width',$(el).css('stroke-width')).css({
        'stroke-dasharray': length+' '+length,
        'stroke-dashoffset': length,
        'stroke-width': 0
    })
})
$('text').css('opacity',0);

//start animation
function animate(){
    $('path').each(function(i,el){
        $(el).css('stroke-width',1).delay(i*150).animate({
            'stroke-dashoffset': 0
        },10*1000 - i*150).animate({
            'stroke-width': $(el).data('width')
        });
    })
    $('text').delay(10*1000).animate({
        opacity: 1
    })
}

//restart animation
$(window).click(function(){
    if($('path').is(':animated')) return;
    
    $('path').each(function(i,el){
        $(el).animate({
            'stroke-dashoffset': el.getTotalLength(),
            'stroke-width': 0
        },8*1000);
    })
    $('text').animate({
        opacity: 0
    })
    
    setTimeout(animate,8*1000);
})

animate();
	// reapeated animations
	function repeatAnim() {
	  var tl = new TimelineMax({ repeat: -1 });
	  tl.to(eyes, .1, { rotationX: 90, transformOrigin: "50% 50%" }, 'repeatStart').
	  to(eyes, .1, { rotationX: 0 }).
	  to(eyes, 3, { rotationX: 0 }).
	  to(eyes, .1, { rotationX: 90, transformOrigin: "50% 50%" }).
	  to(eyes, .1, { rotationX: 0 }).
	  to(eyes, .1, { rotationX: 90, transformOrigin: "50% 50%" }).
	  to(eyes, .1, { rotationX: 0 }).
	  to(eyes, 4, { rotationX: 0 }).
	  to(eyes, .1, { rotationX: 90, transformOrigin: "50% 50%" }).
	  to(eyes, .1, { rotationX: 0 }).
	  to(eyes, 3, { rotationX: 0 }).
	  to(reflection, 1.5, { x: 12, opacity: .15 }, 1, 'repeatStart').
	  to(reflection, 4, { x: 12, opacity: .35 }, 2.5).
	  to(reflection, 1, { x: 0, opacity: .1 }, 4).
	  to(reflection, 4, { x: 0, opacity: .15 }).
	  to(dashed, 15, { rotation: 180, transformOrigin: "50% 50%", ease: Linear.easeNone }, 'repeatStart');


	  return tl;
	}
