<script type="text/javascript">

		$('.send-otp').on('click', function(){
			var mobile = $('input[name="mobile"]').val();
			if(mobile != '' && mobile != NaN && mobile != null && mobile != undefined){
				$.ajax({
					'type' : 'post',
					'url' : 'send-otp.php',
					'data' : 'mobile='+mobile,
					success : function(response){
						var json = JSON.parse(response);
						if(!json.error){
							$('div.e-msg-otp').addClass('success').removeClass('error').text(json.message);
							$('.submit-btn').show();
							$('#otp-field').show();
						}
						else{
							$('div.e-msg-otp').addClass('error').removeClass('success').text(json.message);
							$('.submit-btn').hide();
							$('#otp-field').hide();
						}
					}
				});
			}
			else{
				$('div.e-msg-otp').addClass('error').removeClass('success').text('Please Enter Mobile Number');
			}
		});

		$('form.loginForm').submit(function(e){
			e.preventDefault();
			var formdata = $(this).serialize();
			$.ajax({
				'type' : 'post',
				'url' : 'check-details.php',
				'data' : formdata,
				success : function(response){
					var json = JSON.parse(response);
					if(!json.error){
						$('div.e-msg-submit').addClass('success').removeClass('error').text(json.message);
						$('#rzp-button1').show();
					}
					else{
						$('div.e-msg-submit').addClass('error').removeClass('success').text(json.message);
						$('#rzp-button1').hide();
					}
				}
			});
		});
</script>

